import React from "react";
import {
  CarouselProvider,
  Slider,
  Slide,
  ButtonBack,
  ButtonNext,
} from "pure-react-carousel";
import "pure-react-carousel/dist/react-carousel.es.css";

import Mycard from "./productCard.js";

import "./productCarousel.css";

const Imagecarousel = () => {
  return (
    <CarouselProvider
      visibleSlides={3}
      totalSlides={6}
      step={3}
      naturalSlideWidth={400}
      naturalSlideHeight={500}
      hasMasterSpinner
    >
      <h2 className="headline">
        Simple Carousel with vertically aligned nav buttons
      </h2>
      <p>
        Wrap the &lt;Slider /&gt;, &lt;ButtonBack /&gt;, &lt;ButtonNext /&gt;
        components in a div with relative positioning. Add absolute positioning
        to the buttons.
      </p>
      <div className="container">
        <Slider className="slider">
          <Slide index={0}>
            <ImageWithZoom src="./media/img01.jpeg" />
          </Slide>
          <Slide index={1}>
            <ImageWithZoom src="./media/img02.jpeg" />
          </Slide>
          <Slide index={2}>
            <ImageWithZoom src="./media/img03.jpeg" />
          </Slide>
          <Slide index={3}>
            <ImageWithZoom src="./media/img04.jpeg" />
          </Slide>
          <Slide index={4}>
            <ImageWithZoom src="./media/img05.jpeg" />
          </Slide>
          <Slide index={5}>
            <ImageWithZoom src="./media/img06.jpeg" />
          </Slide>
        </Slider>
        <ButtonBack className="buttonBack">Back</ButtonBack>
        <ButtonNext className="buttonNext">Next</ButtonNext>
      </div>
      <DotGroup className="dotGroup" />
    </CarouselProvider>
  );
};

export default Imagecarousel;
