import React from "react";
import productCard from "./productCard.js";

import "./productCarousel.css";

const Imagecarousel = () => {
  let box = document.querySelector(".product-container");

  const btnpressprev = () => {
    let width = box.clientWidth;
    box.scrollLeft = box.scrollLeft - width;
    console.log(width);
  };

  const btnpressnext = () => {
    let width = box.clientWidth;
    box.scrollLeft = box.scrollLeft + width;
    console.log(width);
  };

  return (
    <div className="product-carousel">
      <button className="pre-btn" onClick={btnpressprev}>
        <p>&lt;</p>
      </button>
      <button className="next-btn" onClick={btnpressnext}>
        <p>&gt;</p>
      </button>

      <div className="product-container">
        <productCard cardno="1" />
        <productCard cardno="2" />
        <productCard cardno="3" />
        <productCard cardno="4" />
        <productCard cardno="5" />
        <productCard cardno="6" />
        <productCard cardno="7" />
        <productCard cardno="8" />
        <productCard cardno="9" />
        <productCard cardno="10" />
        <productCard cardno="11" />
        <productCard cardno="12" />
        <productCard cardno="13" />
      </div>
    </div>
  );
};

export default Imagecarousel;
