import React from "react";
import { useMediaQuery } from "react-responsive";

import {
  CarouselProvider,
  Slider,
  Slide,
  ButtonBack,
  ButtonNext,
} from "pure-react-carousel";
import "pure-react-carousel/dist/react-carousel.es.css";

import Mycard from "./productCard.js";

import "./productCarousel.css";

const Imagecarousel = () => {
  const isDesktopOrLaptop = useMediaQuery({
    query: "(min-width: 1224px)",
  });

  return (
    <CarouselProvider
      visibleSlides={isDesktopOrLaptop ? 6 : 1}
      totalSlides={10}
      step={1}
      naturalSlideWidth={400}
      naturalSlideHeight={500}
    >
      <div>
        <Slider className="slider">
          <Slide index={0}>
            <Mycard cardno="1" />
          </Slide>
          <Slide index={1}>
            <Mycard cardno="2" />
          </Slide>
          <Slide index={2}>
            <Mycard cardno="3" />
          </Slide>
          <Slide index={3}>
            <Mycard cardno="4" />
          </Slide>
          <Slide index={4}>
            <Mycard cardno="5" />
          </Slide>
          <Slide index={5}>
            <Mycard cardno="6" />
          </Slide>
          <Slide index={6}>
            <Mycard cardno="7" />
          </Slide>
          <Slide index={7}>
            <Mycard cardno="8" />
          </Slide>
        </Slider>
        <ButtonBack className="buttonBack">Back</ButtonBack>
        <ButtonNext className="buttonNext">Next</ButtonNext>
      </div>
    </CarouselProvider>
  );
};

export default Imagecarousel;
