import React from "react";
import { useMediaQuery } from "react-responsive";

import {
  CarouselProvider,
  Slider,
  Slide,
  ButtonBack,
  ButtonNext,
} from "pure-react-carousel";
import "pure-react-carousel/dist/react-carousel.es.css";

import Mycard from "./productCard.js";

import "./productCarousel.css";

const Imagecarousel = () => {
  const isDesktopOrLaptop = useMediaQuery({
    query: "(min-width: 1224px)",
  });
  const isBigScreen = useMediaQuery({ query: "(min-width: 1824px)" });

  let totalSlide = () => {
    if (isBigScreen) return 6;
    else if (isDesktopOrLaptop) return 4;
    else if (isTabletOrMobile) return 3;
    else return 1;
  };

  const value = totalSlide();
  console.log(value);

  return (
    <CarouselProvider
      visibleSlides={`${value}`}
      totalSlides={10}
      step={1}
      naturalSlideWidth={400}
      naturalSlideHeight={500}
    >
      <Slider className="slider">
        <Slide index={0}>
          <Mycard cardno="1" />
        </Slide>
        <Slide index={1}>
          <Mycard cardno="2" />
        </Slide>
        <Slide index={2}>
          <Mycard cardno="3" />
        </Slide>
        <Slide index={3}>
          <Mycard cardno="4" />
        </Slide>
        <Slide index={4}>
          <Mycard cardno="5" />
        </Slide>
        <Slide index={5}>
          <Mycard cardno="6" />
        </Slide>
        <Slide index={6}>
          <Mycard cardno="7" />
        </Slide>
        <Slide index={7}>
          <Mycard cardno="8" />
        </Slide>
      </Slider>
      <ButtonBack className="buttonBack">Back</ButtonBack>
      <ButtonNext className="buttonNext">Next</ButtonNext>
    </CarouselProvider>
  );
};

export default Imagecarousel;
